document.addEventListener("DOMContentLoaded",(function(){const scrollToTopBtn=document.getElementById("scrollToTop");function toggleScrollButton(){const scrollY=window.scrollY,windowHeight=window.innerHeight,bodyHeight=document.body.offsetHeight;scrollToTopBtn.style.display=scrollY+windowHeight>=bodyHeight-100?"block":"none"}window.addEventListener("scroll",toggleScrollButton),scrollToTopBtn.addEventListener("click",(function(){window.scrollTo({top:0,behavior:"smooth"}),scrollToTopBtn.style.display="none"})),toggleScrollButton()}));